var mongo = require('mongoskin');
var serverOptions = {
    'auto_reconnect': true,
    'poolSize': 5,
    safe: true
};
var db = mongo.db('localhost:4000/YXKnE', serverOptions);
//var db = mongo.db('localhost:4000/RBCPartner', serverOptions);
var DB = exports.DB = {
    db: db,
    addCollection: function (dbName, collectionName) {
        var _db = mongo.db(dbName, serverOptions);
        DB[collectionName] = _db.collection(collectionName);
    },
    BODefine: db.collection('BODefine'),
    OrgUser:db.collection("OrgUser"),
    KnowledgeDefine:db.collection('KonwledgeDefine'),
    Knowledge:db.collection('Knowledge'),
    KnowledgeContent:db.collection('KnowledgeContent'),
    Question:db.collection('Question'),
    Role:db.collection('Role'),
    TrainningPlan:db.collection('TrainningPlan'),
    TrainningLog:db.collection('TrainningLog'),
    ExamDefine:db.collection('ExamDefine'),
    ExamLog:db.collection('ExamLog'),
    /**
     * 系统功能
     */
    SysFun:db.collection('SysFun'),
    /**
     * 交流
     */
    Communication:db.collection('Communication')
}

